mapper.select:
select ginum, giname,gidesc,vinum,gicredat,gicretim from goods_info where 1=1
and vinum=#{viNum}
order by ginum desc

mapper.delete:
delete from goods_info
where ginum = #{giNum}
	
		and vinum=#{viNum}

mapper.update:
update goods_info
set giname=#{giName}, gidesc=#{giDesc}, vinum=#{viNum}
where ginum = #{giNum}
	
		and vinum=#{viNum}
